					}

					if ( 'blank' === $template-&gt;slug ) {
						$click_class = 'ur-template-select';
					}
					$license_plan_string = 'free';
					// Upgrade checks.
					if ( empty( $license_plan ) &amp;&amp; ! in_array( 'free', $template-&gt;plan, true ) ) {
						$license_plan_string = 'premium';
						$upgrade_class       = 'upgrade-modal';
					} elseif ( ! in_array( str_replace( '-lifetime', '', $license_plan ), $template-&gt;plan, true ) &amp;&amp; ! in_array( 'free', $template-&gt;plan, true ) ) {
						$upgrade_class       = 'ur-template-select';
						$license_plan_string = 'premium';

					}
					/* translators: %s: Template title */
					$template_name = sprintf( esc_attr_x( '%s template', 'Template name', 'user-registration' ), esc_attr( $template-&gt;title ) );
					?&gt;
					<div class="user-registration-template-wrap ur-template" data-plan="&lt;?php echo esc_attr( $license_plan_string ); ?&gt;" id="user-registration-template-&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;">
<figure class="user-registration-screenshot &lt;?php echo esc_attr( $click_class ); ?&gt;" data-template="&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;" data-template-name="&lt;?php echo esc_attr( $template_name ); ?&gt;" data-template-name-raw="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;">
<img alt="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;" src="&lt;?php echo esc_url( $template-&gt;image ); ?&gt;">
<?php echo wp_kses_post( $badge ); ?>
<?php if ( 'blank' !== $template->slug ) : ?&gt;
