<a class="user-registration-btn button-primary &lt;?php echo esc_attr( $upgrade_class ); ?&gt;" data-licence-plan="&lt;?php echo esc_attr( $license_plan ); ?&gt;" data-template="&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;" data-template-name="&lt;?php echo esc_attr( $template_name ); ?&gt;" data-template-name-raw="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;" href="#"><?php esc_html_e( 'Get Started', 'user-registration' ); ?></a>
<a class="user-registration-btn button-secondary ur-template-preview" href="&lt;?php echo esc_url( $preview_link ); ?&gt;" target="_blank"><?php esc_html_e( 'Preview', 'user-registration' ); ?></a>
</div>
<?php endif; ?>
