Function content: // Extract the operator and the number
		preg_match( '/([<>]=?|==|!=|<=|>=)?(\d+)/', $condition, $matches );
		$operator = $matches[1];
		$number   = (int) $matches[2];

		$condition = false;
		switch ( $operator ) {
			case '>':
				$condition = $value > $number;
				break;
			case '>=':
				$condition = $value >= $number;
				break;
			case '<':
				$condition = $value < $number;
				break;
			case '<=':
				$condition = $value <= $number;
				break;
			case '!=':
				$condition = $value != $number;
				break;
			default:
				$condition = $value == $number;
				break;
		}

