			if ( empty( $templates ) ) {
				echo '<div id="message" class="error"><p>' . esc_html__( 'Something went wrong. Please refresh your templates.', 'user-registration' ) . '</p></div>';
			} else {
				foreach ( $templates as $template ) :
					$badge         = '';
					$upgrade_class = 'ur-template-select';
					$preview_link  = isset( $template-&gt;preview_link ) ? $template-&gt;preview_link : '';
					$click_class   = '';
					if ( ! in_array( 'free', $template-&gt;plan, true ) ) {
						if ( in_array( 'personal', $template-&gt;plan, true ) ) {
							$badge_text = esc_html( 'Personal' );
						} elseif ( in_array( 'plus', $template-&gt;plan, true ) ) {
							$badge_text = esc_html( 'Plus' );
						} elseif ( in_array( 'professional', $template-&gt;plan, true ) ) {
							$badge_text = esc_html( 'Professional' );
						}
						$badge = '<span class="user-registration-badge user-registration-badge--success">' . $badge_text . '</span>';
					}

					if ( 'blank' === $template-&gt;slug ) {
						$click_class = 'ur-template-select';
					}
					$license_plan_string = 'free';
					// Upgrade checks.
					if ( empty( $license_plan ) &amp;&amp; ! in_array( 'free', $template-&gt;plan, true ) ) {
						$license_plan_string = 'premium';
						$upgrade_class       = 'upgrade-modal';
					} elseif ( ! in_array( str_replace( '-lifetime', '', $license_plan ), $template-&gt;plan, true ) &amp;&amp; ! in_array( 'free', $template-&gt;plan, true ) ) {
						$upgrade_class       = 'ur-template-select';
						$license_plan_string = 'premium';

					}
					/* translators: %s: Template title */
					$template_name = sprintf( esc_attr_x( '%s template', 'Template name', 'user-registration' ), esc_attr( $template-&gt;title ) );
					?&gt;
					<div class="user-registration-template-wrap ur-template" data-plan="&lt;?php echo esc_attr( $license_plan_string ); ?&gt;" id="user-registration-template-&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;">
<figure class="user-registration-screenshot &lt;?php echo esc_attr( $click_class ); ?&gt;" data-template="&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;" data-template-name="&lt;?php echo esc_attr( $template_name ); ?&gt;" data-template-name-raw="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;">
<img alt="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;" src="&lt;?php echo esc_url( $template-&gt;image ); ?&gt;">
<?php echo wp_kses_post( $badge ); ?>
<?php if ( 'blank' !== $template->slug ) : ?&gt;
								<div class="form-action">
<a class="user-registration-btn button-primary &lt;?php echo esc_attr( $upgrade_class ); ?&gt;" data-licence-plan="&lt;?php echo esc_attr( $license_plan ); ?&gt;" data-template="&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;" data-template-name="&lt;?php echo esc_attr( $template_name ); ?&gt;" data-template-name-raw="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;" href="#"><?php esc_html_e( 'Get Started', 'user-registration' ); ?></a>
<a class="user-registration-btn button-secondary ur-template-preview" href="&lt;?php echo esc_url( $preview_link ); ?&gt;" target="_blank"><?php esc_html_e( 'Preview', 'user-registration' ); ?></a>
</div>
<?php endif; ?>
</img></figure>
<div class="user-registration-form-id-container">
<a class="user-registration-template-name &lt;?php echo esc_attr( $upgrade_class ); ?&gt;" data-licence-plan="&lt;?php echo esc_attr( $license_plan ); ?&gt;" data-template="&lt;?php echo esc_attr( $template-&gt;slug ); ?&gt;" data-template-name="&lt;?php echo esc_attr( $template_name ); ?&gt;" data-template-name-raw="&lt;?php echo esc_attr( $template-&gt;title ); ?&gt;" href="#"><?php echo esc_html( $template->title ); ?&gt;</a>
</div>
</div>
<?php
				endforeach;
			}
